ew = array_slice($rows_temp, $offset*180, 180, true);		}else{			$rows_new = $rows;		}					// echo '<pre>';			// print_r($rows_new);			// exit;		foreach($rows_new as $row){			$birthdate = new DateTime($row['birthday']);        	$today   = new DateTime('today');        	$age = $birthdate->diff($today)->y;			// Profile Image			$profile_image ='';			$query_image = $db->prepare("SELECT *, DATE_FORMAT(created_at, '%Y') as create_year, DATE_FORMAT(created_at, '%m') as create_month, DATE_FORMAT(created_at, '%d') as create_date FROM photos WHERE profile_id = '".$row['id']."' and published ='1' and media_slet_status != '1' ORDER BY position ASC limit 1"); 		$query_image->execute();		$image= '';    	$rows_image = $query_image->fetchAll(PDO::FETCH_ASSOC);		if(count($rows_image) > 0){			// $path = $rows_image[0]['create_year']."/".$rows_image[0]['create_month']."/".$rows_image[0]['create_date']."/".$rows_image[0]['id']."/big_";			// $profile_image = 'https://castit.dk/profile_images/'.$path.$rows_image[0]['image'];			//$profile_image = 'http://castit.dk/assets/profile_images/'.$path.$rows_image[0]['image'];						$min = 0;			$rows_count = count($rows_image);			$max = $rows_count - 1;			$random_index = rand($min, $max);			$random_index = 0;			if (strpos($rows_image[$random_index]['path'], 'vhost') !== false) {				$path = $rows_image[$random_index]['path'];				$profile_image = 'https://castit.dk/images/uploads/'.$rows_image[$random_index]['image'];			}			else{				$path = $rows_image[$random_index]['create_year']."/".$rows_image[$random_index]['create_month']."/".$rows_image[$random_index]['create_date']."/".$rows_image[$random_index]['id']."/big_";				$profile_image = 'https://castit.dk/profile_images/'.$path.$rows_image[$random_index]['image'];			}		}			$profiles[] = array('id' 			=> $row['id'],								'bureau' 		=> $row['bureau'],								'nationality' 	=> $row['nationality'],								'name' 			=> $row['first_name'],								'first_name' 	=>