oc = 1;		if(count($rows_video) > 0){			foreach($rows_video as $row_video){				/*				if (strpos($row_video['path'], 'vhost') !== false) {					$vpath = 'https://castit.dk/images/uploads/'.$row_video['filename'];					$thumbpath = 'https://castit.dk/images/uploads/'.$row_video['filename'];				}				else{					$vpath = 'http://assets3.castit.dk'.$row_video['path']."/".$row_video['filename'];					$thumbpath = 'http://assets3.castit.dk'.$row_video['thumbnail_photo_path']."/".$row_video['thumbnail_photo_filename'];				} */				$vpath = 'http://assets3.castit.dk'.$row_video['path']."/".$row_video['filename'];					$thumbpath = 'http://assets3.castit.dk'.$row_video['thumbnail_photo_path']."/".$row_video['thumbnail_photo_filename'];				$profile_videos[] = array('vidcnt' => $videoc, 'urloriginal' =>$vpath, 'img_thum'=>$thumbpath, 'fullpath'=>$vpath);				$videoc++;			}		}		$skills= '-';		$query_skills = $db->prepare("SELECT s.name FROM skills s JOIN profiles_skills ps ON ps.skill_id = s.id WHERE ps.profile_id = '".$row['id']."'"); 		if($current_language == 'en'){			$query_skills = $db->prepare("SELECT s.name_en as name FROM skills s JOIN profiles_skills ps ON ps.skill_id = s.id WHERE ps.profile_id = '".$row['id']."'"); 		}				$query_skills->execute();		$rows_skills = $query_skills->fetchAll(PDO::FETCH_ASSOC);		if(count($rows_skills) > 0){			foreach($rows_skills as $rows_skill){				$skillsa[] = $rows_skill['name'];			}			$skills=  implode(". ",$skillsa);		}		$categories= '-';		$query_exp = $db->prepare("SELECT c.name FROM categories c JOIN categories_profiles cp ON cp.category_id = c.id WHERE cp.profile_id = '".$row['id']."'"); 				if($current_language == 'en'){			$query_exp = $db->prepare("SELECT c.name_en as name FROM categories c JOIN categories_profiles cp ON cp.category_id = c.id WHERE cp.profile_id = '".$row['id']."'"); 					}				$query_exp->execute();		$rows_exp = $query_exp->fetchAll(PDO::FETCH_ASSOC);		if(count($rows_exp) > 0){		