profile_id`) VALUES ('".$lbid."', '".$profileid."')"; 		//echo $q; 		$query_insert_profile = $db->prepare($q_insert_profile);		$query_insert_profile->execute();	}	$_SESSION["lb_notes"][$profileid] = ($profile_note) ? $profile_note : '';		if($profile_grouping){		$selectedgroupings = explode(',',$profile_grouping);				foreach($selectedgroupings as $key => $gidval){			$query_check_gp = $db->prepare("SELECT * FROM profile_grouping where profile_id = '".$profileid."' AND group_id = '".$gidval."' "); 			$query_check_gp->execute();			$rows_gp = $query_check_gp->fetchAll(PDO::FETCH_ASSOC);			if(count($rows_gp)>0) {				// echo 'ssposdpofsdf';			}else{				$q_insert_profilegp = "INSERT INTO `profile_grouping` ( `profile_id`, `group_id`, `profile_notes`) VALUES ('".$profileid."', '".$gidval."', '".$profile_note."')"; 				//echo $q; 				$query_insert_profilegp = $db->prepare($q_insert_profilegp);				$query_insert_profilegp->execute();			}		}	}					$query_lb_pprofiles = $db->prepare("SELECT * FROM lightboxes_profiles WHERE lightbox_id ='".$lbid."'"); 			$query_lb_pprofiles->execute();			$rows_lb_pprofiles = $query_lb_pprofiles->fetchAll(PDO::FETCH_ASSOC);			$rowcount = count($rows_lb_pprofiles);			if($rowcount > 0){				foreach($rows_lb_pprofiles as $rowp) {					//$lb_pprofiles[] = array('id' => $rowp['profile_id']);											$query = $db->prepare("SELECT p.*, m.profile_group_id, m.profile_number, m.profile_number_first_name_last_name, m.version, m.current, g.name as gender_name, hc.name as hair_color_name, ec.name as eye_color_name FROM profiles p INNER JOIN memberships m ON m.profile_id = p.id INNER JOIN genders g ON g.id = p.gender_id INNER JOIN hair_colors hc ON hc.id = p.hair_color_id INNER JOIN eye_colors ec ON ec.id = p.eye_color_id  WHERE p.id='".$rowp['profile_id']."' AND (p.profile_status_id = '1' ) AND m.current ='1' LIMIT 1"); 						$query->execute();						$rows = $query->fetchAll(PDO::FETCH_ASSOC);						//echo 'sdsds';die;					/