  array();	$grouptoken =  $app->request->get('grouptoken');	$query_grouping = $db->prepare("SELECT * FROM grouping WHERE token_id='".$grouptoken."' AND status ='1' order by group_name"); 	$query_grouping->execute();	$rows_grouping = $query_grouping->fetchAll(PDO::FETCH_ASSOC);	$rowcount = count($rows_grouping);    foreach($rows_grouping as $row) {		$grouping[] = array('id' => $row['group_id'],						  'name' => $row['group_name'],						);	}	$reponse =  array('count'=> $rowcount, 'grouping'=>$grouping);	echoResponse(200, $reponse);});/******************************************Purpose: Add new groupingParameter : Type : GET******************************************/$app->get('/addnewgrouping', function () use ($app) {     global $db;	$groupname =  $app->request->get('groupname');	$grouping = array();	$reponse = array();	$rowcount = 0;	$grouping_token = $app->request->get('grouptoken');		$query_check_gp = $db->prepare("SELECT * FROM `grouping` where `group_name` LIKE '%".$groupname."%' AND token_id ='".$grouping_token."'"); 	$query_check_gp->execute();	$rows_gp = $query_check_gp->fetchAll(PDO::FETCH_ASSOC);	if(count($rows_gp)>0) {		$gpid = $rows_gp[0]['group_id'];	}else{			$q_gruping = "INSERT INTO `grouping` ( `token_id`, `group_name`, `status`) VALUES ('".$grouping_token."', '".$groupname."','1')"; 			//echo $q; 			$gpid = $db->exec($q_gruping);	}		$query_grouping = $db->prepare("SELECT * FROM grouping WHERE status ='1' AND token_id ='".$grouping_token."' order by group_name"); 	$query_grouping->execute();	$rows_grouping = $query_grouping->fetchAll(PDO::FETCH_ASSOC);	$rowcount = count($rows_grouping);    foreach($rows_grouping as $row) {		$grouping[] = array('id' => $row['group_id'],						  'name' => $row['group_name'],						);	}	$reponse =  array('count'=> $rowcount, 'grouping'=>$grouping);	echoResponse(200, $reponse);  });// Landing Page data$app->get('/homepage',function () use ($app) {     global $db