ry->fetchAll(PDO::FETCH_ASSOC);	if((isset($rows)) && count($rows)<= 0){		$q_news = "INSERT INTO `wp_wysija_user` ( `email`, `ip`, `confirmed_ip`, `confirmed_at`, `created_at`, `status`, `domain`) VALUES ('".$email."', '".$ip."', '".$confirmed_ip."', '".$confirmed_at."', '".$created_at."', '".$status."', '".$domain."')";		$query_news = $db->exec($q_news);		if($query_news){			$subscribeid = $query_news;			$q_news1 = "INSERT INTO `wp_wysija_user_list` (`list_id`, `user_id`, `sub_date`, `unsub_date`)VALUES ('1', '".$subscribeid."', '".$confirmed_at."', '0')";			$query_news1 = $db->exec($q_news1);		}		$response = array('sucess' => true, 'message' => 'Thank you for your subscription !', 'type' => 'subscribe');	}	else{	$response = array('sucess' => false, 'message' => 'You have already subscribed with this email !', 'type' => 'subscribe');	}	echoResponse(200, $response);});/******************************************Purpose: Registration step 2 verify OTPParameter : otpType : POST******************************************/$app->post('/resetpassword',function () use ($app) {     global $db;	$data = json_decode($app->request->getBody(), true);	$email = $data['email'];	$query = $db->prepare("SELECT * FROM profiles WHERE email='".$email."' ORDER by id desc limit 1"); 	$query->execute();    $rows = $query->fetchAll(PDO::FETCH_ASSOC);	//print_r($rows);die;	if((isset($rows)) && count($rows)> 0){		if($rows[0]['profile_status_id'] ==1){			$alphabet = 'abdefghklmnpqrstuvwxyzABDEFGHKLMNPQRSTUVWXYZ23456789';			$pass = array(); //remember to declare $pass as an array			$alphaLength = strlen($alphabet) - 1; //put the length -1 in cache			for ($i = 0; $i < 8; $i++) {				$n = rand(0, $alphaLength);				$pass[] = $alphabet[$n];			}			$newpass = implode($pass);			$randompass = md5($newpass);			$q_pwd = "UPDATE `profiles` SET `password`='".$randompass."' WHERE `email`='".$email."'";			$query = $db->prepare($q_pwd);			$query->execute();				$content =