"',							'".$filename."',							'".$filename."',							'".$location."',							'".$location."',							'".$thumbnail."',							'3',							'0',							'".$position."')";					$query_prepared = $db->prepare($query);					$query_prepared->execute();					$recently_updated = "update profiles set recently_updated = 'true' where id = '".$profile_id."'";					$query_prepared = $db->prepare($recently_updated);					$query_prepared->execute();					// $db->exec($recently_updated);    echo json_encode(['status_message'=>'file upload success', 'type'=>'video', 'filename'=>$fileName, 'cdnfilepath'=>$cdnfilepath, 't'=>$time, 'thumbnail'=>$thumbnail, 'zencoder_input'=>$zencoder_input,'zencoder_output'=>$zencoder_output, 'position'=>$position ]);  }});function build_json_zencoder($data_array){	$json = '{	"input": "'.$data_array["input_file"].'",	"outputs": [{		"thumbnails": [				{					"base_url": "'.$data_array["base_url"].'",					"label": "regular",					"number": 1,					"filename": "thumb_'.$data_array["filename"].'",					"public": "true"				}			]		},    {"label": "mp4 high"},    {"url": "'.$data_array["output_file"].'"},    {"h264_profile": "high"}	]	}';	return $json;}//JSON coneverionfunction echoResponse($status_code, $response) {    global $app;    $app->status($status_code);    $app->contentType('application/json');    echo json_encode($response);}function getIP() {        $ip = '';        // cloudFlare IP check        if (isset($_SERVER['HTTP_CF_CONNECTING_IP'])) {            $ip = strip_tags($_SERVER['HTTP_CF_CONNECTING_IP']);        } elseif (!empty($_SERVER['HTTP_X_FORWARDED_FOR']) AND strlen($_SERVER['HTTP_X_FORWARDED_FOR']) > 6) {            $ip = strip_tags($_SERVER['HTTP_X_FORWARDED_FOR']);        } elseif (!empty($_SERVER['HTTP_CLIENT_IP']) AND strlen($_SERVER['HTTP_CLIENT_IP']) > 6) {            $ip = strip_tags($_SERVER['HTTP_CLIENT_IP']);        } elseif (!empty($_SERVER['REMOTE_ADDR']) AND strlen($_SERVER['REMOTE_ADDR']) > 6) {            $ip = strip_tags($_SERVER['REMOTE_ADDR']);        }//endif        if (empty($ip))            $ip = '127.0.0.1';        return strip_tags($ip);    }	//Related functionsfunction generate_uuid() {    return sprintf( '%04x%04x-%04x-%04x-%04x-%04x%04x%04x',        mt_rand( 0, 0xffff ), mt_rand( 0, 0xffff ),        mt_rand( 0, 0xffff ),        mt_rand( 0, 0x0fff ) | 0x4000,        mt_rand( 0, 0x3fff ) | 0x8000,        mt_rand( 0, 0xffff ), mt_rand( 0, 0xffff ), mt_rand( 0, 0xffff )    );}			$app->run();function pp($q){  echo '<pre>';  print_r($q);  echo '</pre>';}function ppe($q){  pp($q);exit;}function unique_code($limit){  return substr(base_convert(sha1(uniqid(mt_rand())), 16, 36), 0, $limit);}function array_sort($array, $on, $order=SORT_ASC){    $new_array = array();    $sortable_array = array();    if (count($array) > 0) {        foreach ($array as $k => $v) {            if (is_array($v)) {                foreach ($v as $k2 => $v2) {                    if ($k2 == $on) {                        $sortable_array[$k] = $v2;                    }                }            } else {                $sortable_array[$k] = $v;            }        }        switch ($order) {            case SORT_ASC:                asort($sortable_array);            break;            case SORT_DESC:                arsort($sortable_array);            break;        }        foreach ($sortable_array as $k => $v) {            $new_array[$k] = $array[$k];        }    }    return $new_array;}?>